// 当前大小
size_t trie_size(const Trie *);

/*
 * 叶子代表有关联数据的节点
 *           .
 *          / \
 *         h   s: s -> value
 *        / \
 *       e   k: hk -> value
 *      /
 *     l: hel -> value
 *
